import React, { useState, useEffect } from "react";
import { Link } from 'react-router-dom'
import { getTodos } from "./api"
import { BsFillTrashFill, BsPencilSquare, BsPlusSquare } from "react-icons/bs";

export const TodoList = () => {
  const [items, setItems] = useState([])

  useEffect(() => {
    const fetchItems = async () => {
      const todos = await getTodos()
      setItems(todos)
    }
    fetchItems()
  }, [])

  return (
    <div className="container">
      <div className="mt-3">
        <h3>Todo List</h3>
        <table className="table table-striped mt-3">
          <thead>
            <tr>
              <th>Text</th>
              <th className="d-flex justify-content-around"><span>Action </span> <Link to="/create" className="float-right" style={{color: "green" }} ><BsPlusSquare />Create Todo</Link></th>
            </tr>
          </thead>
          <tbody>
            {
              items.map(todo => (
                <tr key={todo._id}>
                  <td>
                    {todo.text}
                  </td>
                  <td>
                    <Link  to={`/edit/${todo._id}`}><BsPencilSquare/>Edit</Link>
                    <Link style={{marginLeft: "10px", color: "red"}} to={`/delete/${todo._id}`}><BsFillTrashFill/>Delete</Link>
                  </td>
                </tr>
              ))
            }
          </tbody>
        </table>
      </div>
    </div>
  );
};
