const base_url = (process.env.NODE_ENV === 'production') ? 
'https://6kvg1ametl.execute-api.ap-southeast-1.amazonaws.com/test' :
'http://localhost:4000';

export const getTodos = () => fetch(base_url + "/api/todo").then(res => res.json())

export const createTodo = (todo) => fetch(base_url + "/api/todo/create", {
  method: "POST",
  headers: {
    "Access-Control-Allow-Origin":"*",
    "Accept": "application/json",
    "Content-Type": "application/json"
  },
  body: JSON.stringify(todo)
})  

export const updateTodo = (todo, id) => fetch(base_url + `/api/todo/${id}`, {
  method: "POST",
  headers: {
    "Access-Control-Allow-Origin":"*",
    "Accept": "application/json",
    "Content-Type": "application/json"
  },
  body: JSON.stringify(todo)
})  

export const deleteTodo = (id) => fetch(base_url + `/api/todo/${id}`, {
  method: "DELETE",
  headers: {
    "Access-Control-Allow-Origin":"*",
    "Accept": "application/json",
    "Content-Type": "application/json"
  },
  body: JSON.stringify(todo)
}) 

export const getTodo = (id) => fetch(base_url + `/api/todo/${id}`).then(res => res.json())
