import React, { useState, useEffect } from "react";
import { useRouteMatch, useHistory } from "react-router-dom";
import { getTodo, deleteTodo } from "./api";

export const DeleteTodo = () => {
  const match = useRouteMatch();
  const history = useHistory();
  const [todo, setTodo] = useState();


  useEffect(() => {
    const fetchTodo = async () => {
      const todo = await getTodo(match.params.id)
      setTodo(todo)
    }
    fetchTodo()

  }, [match]);

  const onSubmit = async (data) => {
    await deleteTodo(data, match.params.id)
    history.push("/")
  }
  const onBack = async () => { history.push("/") }
  return todo ? (
    <form onSubmit={onSubmit}>
       <div className="form-group">
        <p>Modal body text goes here.</p>
        <div className="modal-footer">
          <button type="submit" className="btn btn-danger">Confirm Delete</button>
          <button type="button" onClick={onBack} className="btn btn-secondary" data-dismiss="modal">Close</button>
        </div>
        </div>
    </form>
  ) : (
    <div>Loading...</div>
  );
};
